mapper.select:
SELECT product_no as no, category_no as categoryNo, product_brand as brand, product_name as name, product_price as price, product_discount_rate as discountRate, product_discount_YN as discountYN, product_like_count as likeCount, product_sell_unit as sellUnit, product_weight as weight, product_morning_delivery_YN as morningDeliveryYN, product_description as description, product_stock as stock, product_created_date as createdDate, product_status as status, product_image as image, product_sub_title as subTitle FROM TOY_PRODUCTS TP
where product_brand = #{searchCategory}
where product_name like '%'||#{searchWord}||'%'

SELECT product_no as no, category_no as categoryNo, product_brand as brand, product_name as name, product_price as price, product_discount_rate as discountRate, product_discount_YN as discountYN, product_like_count as likeCount, product_sell_unit as sellUnit, product_weight as weight, product_morning_delivery_YN as morningDeliveryYN, product_description as description, product_stock as stock, product_created_date as createdDate, product_status as status, product_image as image, product_sub_title as subTitle FROM ( select row_number() over (order by product_no asc) rn, row_number() over (order by product_name asc) rn, row_number() over (order by product_brand asc) rn, row_number() over (order by product_no desc) rn, product_no, category_no, product_brand, product_name, product_price, product_discount_rate, product_discount_YN, product_like_count, product_sell_unit, product_weight, product_morning_delivery_YN, product_description, product_stock, product_created_date, product_status, product_image, product_sub_title from
toy_products
product_name like '%' || #{keyword} || '%'
and product_ brand like '%' || #{keyword} || '%'
and category_no = #{catNo}
) where rn between #{beginIndex} and #{endIndex}
